mapper.update:
UPDATE TBL_MEETING_POST
SET MEETING_POST_CATEGORY = #{meetingPostCategory}, MEETING_POST_TITLE = #{meetingPostTitle}, MEETING_POST_CONTENT = #{meetingPostTitle}
WHERE ID = #{id}

mapper.select:
SELECT ID, MEMBER_ID, MEETING_POST_CATEGORY, MEETING_POST_TITLE, MEETING_POST_CONTENT
FROM TBL_MEETING_POST
WHERE MEETING_POST_ID = #{meetingPostId}
